import React, { Fragment, useEffect, useState } from 'react';
import { connect, useDispatch } from 'react-redux';
import { getVoteDetails, voteForCandidate } from '../redux/actions/voteActions';
import { showNotification } from '../utils/showNotification';

const VoterDashboard = ({ voteDetails, electionStatus, candidates, loading }) => {
    const [voterConstituency, setVoterConstituency] = useState('');
    const [voterId, setVoterId] = useState('');
    const [candidateId, setCandidateId] = useState('');

    const dispatch = useDispatch();

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user'));
        if (user && user.voter && user.voter.voterId) {
            setVoterId(user.voter.voterId);
            setVoterConstituency(user?.voter?.constituency?.constituencyName);
            dispatch(getVoteDetails(user.voter.voterId));
        }
    }, [dispatch]);

    const handleVoteForCandidate = (e) => {
        e.preventDefault();
        if (!candidateId) {
            showNotification('danger', 'You must select a candidate before voting');
            return;
        }
        dispatch(voteForCandidate({ voterId, candidateId }));
    };

    const displayUI = (electionStatus) => {
        console.log('election status', electionStatus)
        switch (electionStatus) {
            case 'NOT_STARTED':
                return <h5 className='my-5 alert alert-info p-3' role='alert'>Election has not yet begun. Kindly check back to see when the admin starts the election</h5>;
            case 'COMPLETED':
                return <h5 className='my-5 alert alert-info p-3' role='alert'>Election voting has ended. Results are incoming to be announced by Electoral Officer</h5>;
            case 'PENDING':
            default:
                return (
                    <div className="card text-left mt-5">
                        <div className="card-header">
                            Member of Parliament Election for Constituency {voterConstituency}
                        </div>
                        <div className="card-body p-5">
                            {!!voteDetails ? (
                                <h5>You have already voted in this constituency</h5>
                            ) : (
                                <Fragment>
                                    {!candidates || candidates.length === 0 ? (
                                        <h5>There are no candidates contesting in this constituency</h5>
                                    ) : (
                                        <Fragment>
                                            {candidates.map(({ candidate, candidateId }) => (
                                                <div className="row d-flex align-items-center pointer w-100 ml-0 mb-3" key={candidateId}>
                                                    <input type='radio' id={`Candidate ${candidateId}`} name='candidateId' value={candidateId} onChange={({ target }) => setCandidateId(target.value)} />
                                                    <label className='ml-3 mb-0 pointer' htmlFor={`Candidate ${candidateId}`}>{candidate}</label>
                                                </div>
                                            ))}
                                            <div className="form-group my-3 py-3">

                                                <button type="submit" onClick={handleVoteForCandidate} name="submit"  className="btn btn-md btn-danger pointer">
                                                    { loading ? (                                                    
                                                        <span className="spinner-border spinner-border-sm mr12" id="voteQuestion-btn-loader" role="status" aria-hidden="true"></span>
                                                        ): (<i className="bi bi-check-circle mr12" id="voteQuestion-btn-icon"></i>) }
                                                    Vote
                                                </button>
                                            </div>
                                        </Fragment>
                                    )}
                                </Fragment>
                            )}
                        </div>
                    </div>
                );
        }
    };

    return (
        <div className='text-left mt-5'>
            <h3>Voter Dashboard</h3>
            {displayUI(electionStatus)}
        </div>
    );
};

const mapStateToProps = ({ vote, loading }) => {
    return {
        electionStatus: vote && vote.election && vote.election.status,
        candidates: vote && (vote.candidates),
        voteDetails: vote && vote.vote,
        loading
    };
};

export default connect(mapStateToProps, { getVoteDetails, voteForCandidate })(VoterDashboard);
