import axios from '../../utils/axiosConfig'
import { GET_CONSTITUENCIES } from "./types";

const defaultConstituencies = [
    {
        constituencyName: "Shangri-la-Town",
        constituencyId: 1
    },
    {
        constituencyName: "Northern-Kunlun-Mountain",
        constituencyId: 2
    },
    {
        constituencyName: "Western-Shangri-la",
        constituencyId: 3
    },
    {
        constituencyName: "Naboo-Vallery",
        constituencyId: 4
    },

]

export const getConstituencies = () => async dispatch => {
    try {
        const { data } = await axios.get('/constituency')
        return dispatch(handleGetConstituencies(data));
    } catch (err) {
        return dispatch(defaultConstituencies)
    }

}

export function handleGetConstituencies(constituencies) {
    return {
        type: GET_CONSTITUENCIES,
        payload: constituencies
    }
}
