import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";
import { handleRegisterUser } from "../redux/actions/authedActions";
import { redirectUserBackToHomeIfLoggedIn } from "../utils/api";
import { getConstituencies } from '../redux/actions/constituencyActions' 
import { showNotification } from "../utils/showNotification";
import isValidEmail from "../utils/EmailUtil";
import { QrScanner } from '@yudiel/react-qr-scanner'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import QRCodeScanner from "./QRCodeScanner";

class Register extends Component {
  state = {
    voterId: "",
    fullName: "",
    dateOfBirth: "",
    password: "",
    constituency: "",
    UVC: "",
    errors: {},
    showModal: false
  };

  handleDecode = (result) => {
    console.log(result);
    this.setState({ UVC: result })
    this.toggleModal();
  }

  handleClose = () => this.setState({ showModal: false })

  toggleModal = () => {
    console.log(this.state)
    this.setState(state => ({ showModal: !state.showModal }))
  }

  handleChange = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
    console.log('name ', name, 'value ', value)
  }

  handleUpdateUVC = UVC => {
    if (!UVC) return;
    this.setState({ UVC })
    this.handleClose()
  }


  registerUser = (e) => {
    e.preventDefault();
    const { voterId, fullName, dateOfBirth, password, constituency, UVC, showModal } = this.state;
    const { history, handleRegisterUser } = this.props;
    if (!voterId) {
        showNotification('danger', 'The voterId is required')
        return;
    } else if (!isValidEmail(voterId)) {
        showNotification('danger', 'The voterId has an invalid email format')
        return;
    }
    if (UVC && UVC.length != 8) {
        showNotification('danger', 'The UVC code is an 8 digit number. Kindly check again')
        return;
    }
    console.log('DOB ', dateOfBirth)

    if (!!dateOfBirth && new Date() <= new Date(dateOfBirth)) {
        showNotification('danger', 'The date of birth can not be in the past. Kindly check again')
        return;
    }

    const voterToRegister = { voterId, fullName, dateOfBirth, password, constituencyId: constituency, uvc: UVC }
    handleRegisterUser(voterToRegister, { history });
  };

  componentDidMount() {
    const { history } = this.props;
    this.props.getConstituencies();
    redirectUserBackToHomeIfLoggedIn(history);
  }

  isRegisterationFormInvalid = (e) => {
    return (
      (!this.state.voterId || !this.state.voterId.trim()) ||
      (!this.state.fullName || !this.state.fullName.trim()) ||
      (!this.state.dateOfBirth || !this.state.dateOfBirth.trim()) ||
      (!this.state.password || !this.state.password.trim()) ||
      (!this.state.constituency || !this.state.constituency.trim()) ||
      (!this.state.UVC || !this.state.UVC.trim())
    );
  };

  render() {
    const { voterId, fullName, dateOfBirth, password, UVC, constituency, errors, showModal } = this.state;
    const { constituencies, loading } = this.props

    return (
      <div className="row">
        <div className="col-lg-6 offset-lg-3">
          <div className="card mt-5 text-center">
            <div className="card-header text-center">
              <h3>Welcome to the General Election Voting System!</h3>
            </div>
            <div className="card-body">
              <h3 className="theme-text text-center">Register</h3>
              <form onSubmit={this.registerUser}>
                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="voterId">
                    Voter ID<span className="text-danger">*</span>
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="voterId"
                    name="voterId"
                    value={voterId}
                    onChange={this.handleChange}
                    placeholder='Enter email address e.g. user@gevs.com'
                  />
                  <span className="text-danger"> { errors[voterId] && errors[voterId]} </span>
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="fullName">
                    Full Name<span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="fullName"
                    name="fullName"
                    value={fullName}
                    onChange={this.handleChange}
                    placeholder='Enter your full name e.g. Olatunde J'
                  />
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="dateOfBirth">
                    Date of Birth<span className="text-danger">*</span>
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="dateOfBirth"
                    name="dateOfBirth"
                    value={dateOfBirth}
                    onChange={this.handleChange}
                  />
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="password">
                    Password<span className="text-danger">*</span>
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    name="password"
                    value={password}
                    onChange={this.handleChange}
                    placeholder='Enter your password'
                  />
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="constituency">
                    Constituency<span className="text-danger">*</span>
                  </label>
                  <select
                    name="constituency"
                    className="form-control"
                    value={constituency}
                    onChange={this.handleChange}
                  >
                    <option value="">Select a constituency</option>
                    {constituencies.map(({ constituencyName, constituencyId }) => (
                      <option value={constituencyId} key={constituencyId}>
                        {" "}
                        {constituencyName}{" "}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="UVC">
                    Unique Voter Code(UVC)<span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="UVC"
                    name="UVC"
                    value={UVC}
                    onChange={this.handleChange}
                    placeholder='Enter your UVC e.g. AAAAAAAA'
                  />
                  <div className="w-100 d-flex flex-column justify-content-center align-items-center">
                    <div className="my-3">OR</div>
                    <div className="w-100 text-danger pointer" data-toggle="modal" data-target="#qrCodeScanner" onClick={this.toggleModal}>
                        <i className="bi bi-search"></i> Scan QR Code
                    </div>
                  </div>
                </div>


                <div className="form-group my-4">
                  <button
                    type="submit"
                    disabled={this.isRegisterationFormInvalid()}
                    className="btn btn-block btn-danger"
                  >
                    {
                        loading ? (<span className="spinner-border spinner-border-sm mr12" id="registerForm-btn-loader" role="status" aria-hidden="true"></span>)
                        : (<i className="bi bi-person-plus mr12" id="registerForm-btn-icon"></i>)
                    }
                    Register
                    </button>
                </div>

                <p>
                    Already registered ?
                    <Link to="/login" className="text-danger"> Sign In</Link>
                </p>



                <Modal show={showModal} onHide={this.handleClose} size='lg' centered className="qr-code-scanner-container" >
                    <Modal.Header closeButton>
                    <Modal.Title>GEVS QR CODE SCANNER</Modal.Title>
                    </Modal.Header>
                    <Modal.Body className="d-flex justify-content-center align-items-center">
                        <QRCodeScanner handleUpdateUVC={this.handleUpdateUVC} />
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" onClick={this.handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ constituencies, loading }) => {
    return {
        constituencies,
        loading
    }
}

export default connect(mapStateToProps, { handleRegisterUser, getConstituencies })(withRouter(Register));
