import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";
import { handleRegisterUser } from "../redux/actions/authedActions";
import { redirectUserBackToHomeIfLoggedIn } from "../utils/api";
import { showNotification } from "../utils/showNotification";
import isValidEmail from "../utils/EmailUtil";

class Register extends Component {
  state = {
    firstName: "",
    lastName: "",
    studentNo: "",
    password: "",
    confirmPassword: "",
    errors: {},
    showModal: false
  };

  handleClose = () => this.setState({ showModal: false })

  toggleModal = () => {
    console.log(this.state)
    this.setState(state => ({ showModal: !state.showModal }))
  }

  handleChange = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
  }


  registerUser = (e) => {
    e.preventDefault();
    const { firstName, lastName, email, studentNo, dateOfBirth, password, confirmPassword, showModal } = this.state;
    const { history, handleRegisterUser } = this.props;
    if (!firstName) {
        showNotification('danger', 'The first name field is required')
        return;
    } else if (!lastName) {
      showNotification('danger', 'The last name field is required')
      return;
    } else if (!studentNo) {
      showNotification('danger', 'The student number field is required')
      return;
    } else if (!isValidEmail(email)) {
        showNotification('danger', 'The email is invalid')
        return;
    }

    const userToRegister = { firstName, lastName,  dateOfBirth, password, confirmPassword, email }
    handleRegisterUser(userToRegister, { history });
  };

  componentDidMount() {
    const { history } = this.props;
    redirectUserBackToHomeIfLoggedIn(history);
  }

  isRegisterationFormInvalid = (e) => {
    return (
      (!this.state.voterId || !this.state.voterId.trim()) ||
      (!this.state.fullName || !this.state.fullName.trim()) ||
      (!this.state.dateOfBirth || !this.state.dateOfBirth.trim()) ||
      (!this.state.password || !this.state.password.trim()) ||
      (!this.state.constituency || !this.state.constituency.trim()) ||
      (!this.state.UVC || !this.state.UVC.trim())
    );
  };

  render() {
    const { firstName, lastName, studentNo, email, fullName, dateOfBirth, password, UVC, constituency, errors, showModal } = this.state;
    const { loading } = this.props

    return (
      <div className="row">
        <div className="col-lg-6 offset-lg-3">
          <div className="card mt-5 text-center">
            <div className="card-header text-center">
              <h3>Create Your Account</h3>
              <div className="social-login">
                <i className="bi bi-github"></i>
                <i className="bi bi-facebook mx-3 my-3"></i>
                <i className="bi bi-google"></i>
              </div>
              <div className="or-signup-section">
                <hr className="custom-header"/> <span style={{ fontSize: '14px'}}>or signup with</span><hr className="custom-header"/>
              </div>
            </div>
            <div className="card-body">
              <form onSubmit={this.registerUser}>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="fullName">
                    First Name<span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="firstName"
                    name="firstName"
                    value={firstName}
                    onChange={this.handleChange}
                    placeholder='Enter your first name e.g. Olatunde'
                  />
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="lastName">
                    Last Name<span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="lastName"
                    name="lastName"
                    value={lastName}
                    onChange={this.handleChange}
                    placeholder='Enter your last name e.g. John'
                  />
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="studentNo">
                    Student Number<span className="text-danger">*</span>
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="studentNo"
                    name="studentNo"
                    value={studentNo}
                    onChange={this.handleChange}
                    placeholder='Enter your 12 digit student number e.g. 111111111111'
                  />
                  <span className="text-danger"> { errors[studentNo] && errors[studentNo]} </span>
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="email">
                    Email<span className="text-danger">*</span>
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                    value={email}
                    onChange={this.handleChange}
                    placeholder='Enter your email'
                  />
                  <span className="text-danger"> { errors[email] && errors[email]} </span>
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="password">
                    Password<span className="text-danger">*</span>
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    name="password"
                    value={password}
                    onChange={this.handleChange}
                    placeholder='Enter your password'
                  />
                </div>

                <div className="form-group mb-3 d-flex flex-column align-items-start">
                  <label htmlFor="password">
                    Confirm Password<span className="text-danger">*</span>
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    name="password"
                    value={password}
                    onChange={this.handleChange}
                    placeholder='Re-enter password'
                  />
                </div>

                <div className="form-group my-4">
                  <button
                    type="submit"
                    disabled={this.isRegisterationFormInvalid()}
                    className="btn btn-block btn-danger"
                  >
                    {
                        loading ? (<span className="spinner-border spinner-border-sm mr12" id="registerForm-btn-loader" role="status" aria-hidden="true"></span>)
                        : (<i className="bi bi-person-plus mr12" id="registerForm-btn-icon"></i>)
                    }
                    Sign up
                    </button>
                </div>

                <p>
                    Already registered ?
                    <Link to="/login" className="text-danger"> Sign In</Link>
                </p>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ loading }) => {
    return {
        loading
    }
}

export default connect(mapStateToProps, { handleRegisterUser })(withRouter(Register));
